function startReading(){container=document.querySelector("#topelement");inputTxt="";inputs=container.querySelectorAll("p:not(.no-read), h1:not(.no-read), h2:not(.no-read), h3:not(.no-read), a:not(.no-read)");inputs.forEach(n=>{hasSomeParentTheClass(n,"no-read")==!1&&(inputTxt=inputTxt+" "+n.innerText)});utterThis=new SpeechSynthesisUtterance(inputTxt);utterThis.rate=.85;utterThis.onpause=n=>{n.utterance.text.charAt(n.charIndex)};synth.speak(utterThis)}function hasSomeParentTheClass(n,t){return n!=null&&n.classList!=null&&n.classList.contains(t)==!0?!0:n.parentNode==null?!1:hasSomeParentTheClass(n.parentNode,t)}function stopReading(){synth.cancel()}const synth=window.speechSynthesis;